// src/pages/Register.js
import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { registerUser, loginUser } from "../services/authService"; // ‚Üê importamos loginUser
import styled from "styled-components";
import { InputGroup, Form } from "react-bootstrap";

// Regex para validar contrase√±a
const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\W).{8,}$/;

const Container = styled.div`
  max-width: 400px;
  margin: 3rem auto;
  padding: 2rem;
  background: white;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
`;
const Title = styled.h2`
  text-align: center;
  margin-bottom: 1.5rem;
`;
const StyledForm = styled(Form)`
  display: flex;
  flex-direction: column;
`;
const Input = styled(Form.Control)`
  margin-bottom: 1rem;
  padding: 0.5rem;
  font-size: 1rem;
`;
const StyledSelect = styled(Form.Select)`
  margin-bottom: 1rem;
  padding: 0.5rem;
  font-size: 1rem;
`;
const StyledButton = styled.button`
  padding: 0.75rem;
  font-size: 1rem;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover { background: #0056b3; }
`;
const SuccessMessage = styled.div`
  margin-bottom: 1rem;
  padding: 0.75rem;
  text-align: center;
  color: #155724;
  background: #d4edda;
  border: 1px solid #c3e6cb;
  border-radius: 4px;
`;
const ErrorMessage = styled.div`
  margin-bottom: 1rem;
  padding: 0.75rem;
  text-align: center;
  color: #721c24;
  background: #f8d7da;
  border: 1px solid #f5c6cb;
  border-radius: 4px;
`;

const EyeToggle = styled(InputGroup.Text)`
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.5rem;
  background: white;
  border-left: 0;
  border-top-right-radius: .25rem;
  border-bottom-right-radius: .25rem;
  cursor: pointer;
`;

const PasswordField = styled(Form.Control)`
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
`;

export default function Register() {
  const navigate = useNavigate();

  // datos del registro nuevo
  const [form, setForm] = useState({
    nombre: "",
    email: "",
    telefono: "",
    password: "",
    confirmPassword: "",
    red_social: "",
    rol: "paciente",
  });

  // credenciales del admin existente
  const [adminCreds, setAdminCreds] = useState({
    adminEmail: "",
    adminPassword: ""
  });

  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");
  const [submitting, setSubmitting] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);

  const onChange = e => {
    const { name, value } = e.target;
    if (name === "adminEmail" || name === "adminPassword") {
      setAdminCreds(c => ({ ...c, [name]: value }));
    } else {
      setForm(f => ({ ...f, [name]: value }));
    }
    setError("");
  };

  const onSubmit = async e => {
    e.preventDefault();
    setError("");

    // 1) Validar contrase√±a
    if (!passwordRegex.test(form.password)) {
      setError("La contrase√±a debe tener m√≠nimo 8 caracteres, al menos una may√∫scula, una min√∫scula y un car√°cter especial.");
      return;
    }
    if (form.password !== form.confirmPassword) {
      setError("La confirmaci√≥n de contrase√±a no coincide.");
      return;
    }

    setSubmitting(true);

    // 2) Si quieren crear un Admin, primero autenticamos al Admin existente
    if (form.rol === "administrador") {
      if (!adminCreds.adminEmail || !adminCreds.adminPassword) {
        setError("Debes ingresar email y contrase√±a de un Administrador existente.");
        setSubmitting(false);
        return;
      }
      try {
        // loginUser lanza error si falla
        await loginUser({
          email: adminCreds.adminEmail,
          password: adminCreds.adminPassword
        });
      } catch (err) {
        setError(err.message || "Autenticaci√≥n de Admin fallida.");
        setSubmitting(false);
        return;
      }
    }

    // 3) Ya validado (o rol != admin), procedemos a registrar
    try {
      await registerUser({
        nombre: form.nombre,
        email: form.email,
        telefono: form.telefono,
        password: form.password,
        red_social: form.red_social,
        rol: form.rol
      });

      setSuccess("Registro creado con √©xito. En breve ser√°s redirigido al inicio de sesi√≥n...");
      setTimeout(() => navigate("/login", { replace: true }), 3000);
    } catch (err) {
      setError(err.message || "Ocurri√≥ un error al registrar.");
      setSubmitting(false);
    }
  };

  return (
    <Container>
      <Title>Registro de Usuario</Title>
      {error && <ErrorMessage>{error}</ErrorMessage>}
      {success && <SuccessMessage>{success}</SuccessMessage>}

      {!success && (
        <StyledForm onSubmit={onSubmit}>
          <Input
            name="nombre"
            placeholder="Nombre completo"
            value={form.nombre}
            onChange={onChange}
            disabled={submitting}
            required
          />
          <Input
            name="email"
            type="email"
            placeholder="Correo electr√≥nico"
            value={form.email}
            onChange={onChange}
            disabled={submitting}
            required
          />
          <Input
            name="telefono"
            placeholder="Tel√©fono"
            value={form.telefono}
            onChange={onChange}
            disabled={submitting}
            required
          />

          <InputGroup className="mb-3">
            <PasswordField
              type={showPassword ? "text" : "password"}
              name="password"
              placeholder="Contrase√±a"
              value={form.password}
              onChange={onChange}
              disabled={submitting}
              required
            />
            <EyeToggle onClick={() => setShowPassword(v => !v)}>
              {showPassword ? "üôà" : "üëÅÔ∏è"}
            </EyeToggle>
          </InputGroup>

          <InputGroup className="mb-3">
            <PasswordField
              type={showConfirm ? "text" : "password"}
              name="confirmPassword"
              placeholder="Confirmar contrase√±a"
              value={form.confirmPassword}
              onChange={onChange}
              disabled={submitting}
              required
            />
            <EyeToggle onClick={() => setShowConfirm(v => !v)}>
              {showConfirm ? "üôà" : "üëÅÔ∏è"}
            </EyeToggle>
          </InputGroup>

          <Input
            name="red_social"
            placeholder="Red social (opcional)"
            value={form.red_social}
            onChange={onChange}
            disabled={submitting}
          />

          <StyledSelect
            name="rol"
            value={form.rol}
            onChange={onChange}
            disabled={submitting}
            required
          >
            <option value="paciente">Paciente</option>
            <option value="medico">M√©dico</option>
            <option value="administrador">Administrador</option>
          </StyledSelect>

          {form.rol === "administrador" && (
            <>
              <hr />
              <Input
                name="adminEmail"
                type="email"
                placeholder="Admin existente: Email"
                value={adminCreds.adminEmail}
                onChange={onChange}
                disabled={submitting}
                required
              />
              <Input
                name="adminPassword"
                type="password"
                placeholder="Admin existente: Contrase√±a"
                value={adminCreds.adminPassword}
                onChange={onChange}
                disabled={submitting}
                required
              />
            </>
          )}

          <StyledButton type="submit" disabled={submitting}>
            {submitting ? "Procesando..." : "Registrarse"}
          </StyledButton>
        </StyledForm>
      )}

      <p style={{ marginTop: '1rem', textAlign: 'center' }}>
        ¬øYa tienes una cuenta? <Link to="/login">Inicia sesi√≥n aqu√≠</Link>
      </p>
    </Container>
  );
}

