import React, { useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { searchDoctors } from "../services/doctorService";
import { createAppointment } from "../services/appointmentService";
import { Container, Form, Row, Col, Card, Button, Spinner, Modal } from "react-bootstrap";
import LogoutButton from "../components/LogoutButton";
import { getPerfilFilters } from "../services/filterService";

export default function Dashboard() {
  const { user } = useContext(AuthContext);
  const [filters, setFilters] = useState({ especialidad: "", ubicacion: "", seguro_medico: "" });
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(false);

  // Modal de agendamiento
  const [showModal, setShowModal] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [fechaHora, setFechaHora] = useState("");
  const [notiMethod, setNotiMethod] = useState("email");

  const handleFilterChange = e => {
    setFilters(f => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleSearch = async e => {
    e.preventDefault();
    setLoading(true);
    try {
      const docs = await searchDoctors(filters);
      setDoctors(docs);
    } catch (err) {
      console.error("Error buscando médicos:", err);
      alert(err.message || "Error al buscar médicos");
    } finally {
      setLoading(false);
    }
  };

  const openModal = doctor => {
    setSelectedDoctor(doctor);
    setFechaHora("");
    setShowModal(true);
  };

  const handleAppointment = async () => {
    if (!fechaHora) {
      alert("Por favor selecciona fecha y hora");
      return;
    }
    try {
      await createAppointment({
        id_paciente: user.id_usuario,
        id_medico: selectedDoctor.id_usuario,
        fecha_hora: fechaHora,
        metodo_notificacion: notiMethod,
        seguro_medico: selectedDoctor.seguro_medico || "",
      });
      alert("Cita agendada con éxito");
      setShowModal(false);
    } catch (err) {
      console.error("Error agendando cita:", err);
      alert(err.message || "Error al agendar cita");
    }
  };

  return (
    <Container className="py-5">
      <div className="d-flex mb-3">
        <h2 className="mb-4">Buscar Médicos</h2>
        <LogoutButton />
      </div>

      <Form onSubmit={handleSearch}>
        <Row className="g-3">
          <Col md>
            <Form.Control
              name="especialidad"
              placeholder="Especialidad"
              value={filters.especialidad}
              onChange={handleFilterChange}
            />
          </Col>
          <Col md>
            <Form.Select
              name="especialidad"
              value={filters.especialidad}
              onChange={handleFilterChange}
            >
              <option value="">-- Especialidad --</option>
              {listaEspecialidades.map(sp => (
                <option key={sp} value={sp}>{sp}</option>
              ))}
            </Form.Select>
          </Col>
          <Col md>
            <Form.Control
              name="ubicacion"
              placeholder="Ubicación"
              value={filters.ubicacion}
              onChange={handleFilterChange}
            />
          </Col>
          <Col md>
            <Form.Select
              name="ubicacion"
              value={filters.ubicacion}
              onChange={handleFilterChange}
            >
              <option value="">-- Ubicación --</option>
              {listaUbicaciones.map(loc => (
                <option key={loc} value={loc}>{loc}</option>
              ))}
            </Form.Select>
          </Col>
          <Col md>
            <Form.Control
              name="seguro_medico"
              placeholder="Seguro médico"
              value={filters.seguro_medico}
              onChange={handleFilterChange}
            />
          </Col>
          <Col md="auto">
            <Button type="submit" disabled={loading}>
              {loading ? <Spinner animation="border" size="sm" /> : "Buscar"}
            </Button>
          </Col>
        </Row>
      </Form>

      <Row className="mt-4 g-3">
        {doctors.map(doc => (
          <Col key={doc.id_usuario} xs={12} md={6} lg={4}>
            <Card>
              <Card.Body>
                <Card.Title>{doc.nombre}</Card.Title>
                <Card.Text>
                  <strong>Especialidad:</strong> {doc.PerfilMedico?.especialidad}<br />
                  <strong>Ubicación:</strong> {doc.PerfilMedico?.ubicacion}
                </Card.Text>
                <Button variant="primary" onClick={() => openModal(doc)}>
                  Agendar Cita
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {/* Modal de agendamiento */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Agendar con {selectedDoctor?.nombre}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group className="mb-3">
            <Form.Label>Fecha y hora</Form.Label>
            <Form.Control
              type="datetime-local"
              value={fechaHora}
              onChange={e => setFechaHora(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Método de notificación</Form.Label>
            <Form.Select value={notiMethod} onChange={e => setNotiMethod(e.target.value)}>
              <option value="email">Email</option>
              <option value="sms">SMS</option>
              <option value="push">Push</option>
            </Form.Select>
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancelar
          </Button>
          <Button variant="primary" onClick={handleAppointment}>
            Confirmar Cita
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
