// src/pages/Home.js
import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import heroImg from '../images/medical-hero-image-1024x576.jpg';

// Este Styled Component sólo para darle un pequeño margen al contenedor
const Wrapper = styled(Container).attrs({ fluid: true })`
  width: 100%;
  height: 100vh;
  padding: 0 !important;
  margin: 0 !important;
`

const Img = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`

export default function Home() {
  return (
    <Wrapper className="d-flex align-items-center py-5">
      <Row className="w-100 gx-5">
        {/* Columna Izquierda: la imagen */}
        <Col md={6} className="mb-4 mb-md-0">
          {/* Pon aquí tu ruta real, p.ej. /images/medical_illustration.png */}
          <Img
            src={heroImg}
            alt="Ilustración gestión citas"
          />
        </Col>

        {/* Columna Derecha: tarjeta con título y botones */}
        <Col md={6}>
          <Card className="shadow-sm">
            <Card.Body className="p-4">
              <Card.Title as="h1" className="text-center mb-3">
                Bienvenido a QuickCita
              </Card.Title>
              <Card.Text className="text-center mb-4">
                Tu sistema de gestión de citas médicas
              </Card.Text>
              <div className="d-flex justify-content-end gap-2">
                <Button
                  as={Link}
                  to="/login"
                  variant="primary"
                >
                  Iniciar Sesión
                </Button>
                <Button
                  as={Link}
                  to="/register"
                  variant="outline-primary"
                >
                  Registro
                </Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Wrapper>
  );
}
