import React, { useEffect, useContext } from "react"
import { Nav } from "react-bootstrap"
import { NavLink, Outlet, useNavigate } from "react-router-dom"
import { AuthContext } from "../context/AuthContext"
import LogoutButton from "../components/LogoutButton"

export default function MedicoHome() {
  const { user } = useContext(AuthContext)
  const navigate = useNavigate()

  useEffect(() => {
    // Ejemplo: si el perfil no está completo, forzar validar
    // fetch(`/api/perfiles_medicos/${user.id_usuario}`)
    //   .then(r => r.json())
    //   .then(perf => {
    //     if (!perf.cedula_profesional) {
    //       navigate("validar", { replace: true })
    //     }
    //   })
  }, [user.id_usuario, navigate])

  return (
    <>
      <div className="d-flex justify-content-end p-3">
        <LogoutButton />
      </div>

      <Nav variant="tabs" className="justify-content-center mb-3">
        <Nav.Item>
          <Nav.Link
            as={NavLink}
            to=""      // ruta index → /medico
            end
          >
            Validar Perfil
          </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link
            as={NavLink}
            to="calendario"
          >
            Calendario
          </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link
            as={NavLink}
            to="alertas"
          >
            Alertas
          </Nav.Link>
        </Nav.Item>
      </Nav>

      <div className="p-4">
        <Outlet />
      </div>
    </>
  )
}
