// frontend/src/pages/Register.js
import React, { useState } from "react";
import { registerUser } from "../services/authService";

const Register = () => {
  const [form, setForm] = useState({
    nombre: "",
    email: "",
    telefono: "",
    rentrasena: "",
    red_social: "",
  });

  const onChange = e =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      const user = await registerUser(form);
      console.log("Usuario registrado:", user);
      // redirigir a login u otra página...
    } catch (err) {
      console.error("Error al registrar:", err);
    }
  };

  return (
    <div>
      <h2>Registro</h2>
      <form onSubmit={onSubmit}>
        <input name="nombre" placeholder="Nombre" onChange={onChange} />
        <input name="email" type="email" placeholder="Email" onChange={onChange} />
        <input name="telefono" placeholder="Teléfono" onChange={onChange} />
        <input name="rentrasena" type="password" placeholder="Contraseña" onChange={onChange} />
        <input name="red_social" placeholder="Red social (opcional)" onChange={onChange} />
        <button type="submit">Registrarse</button>
      </form>
    </div>
  );
};

export default Register;
