import React, { useState, useEffect, useContext } from "react";
import { Form, Button, Container, Spinner, Alert } from "react-bootstrap";
import { AuthContext } from "../context/AuthContext";
import api from "../services/api";

export default function ValidarPerfil() {
  const { user } = useContext(AuthContext);
  const [perfil, setPerfil] = useState(null);
  const [form, setForm] = useState({
    cedula_profesional: "",
    especialidad: "",
    ubicacion: "",
    seguro_medico: "",
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    // Traer perfil existente
    api.get(`/perfiles_medicos/${user.id_usuario}`)
      .then(res => {
        setPerfil(res.data);
        setForm({
          cedula_profesional: res.data.cedula_profesional || "",
          especialidad:       res.data.especialidad       || "",
          ubicacion:          res.data.ubicacion          || "",
          seguro_medico:      res.data.seguro_medico      || "",
        });
      })
      .catch(() => setError("No se pudo cargar el perfil"))
      .finally(() => setLoading(false));
  }, [user.id_usuario]);

  const handleChange = e => {
    setForm(f => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    setSaving(true);
    api.put(`/perfiles_medicos/${user.id_usuario}`, form)
      .then(res => {
        setPerfil(res.data);
        setError("");
        alert("Perfil actualizado con éxito");
      })
      .catch(() => setError("Error al guardar"))
      .finally(() => setSaving(false));
  };

  if (loading) return <Container className="py-5 text-center"><Spinner animation="border" /></Container>;

  return (
    <Container className="py-5" style={{ maxWidth: 600 }}>
      <h3>Validar/Completar Perfil Médico</h3>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Cédula profesional</Form.Label>
          <Form.Control
            name="cedula_profesional"
            value={form.cedula_profesional}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Especialidad</Form.Label>
          <Form.Control
            name="especialidad"
            value={form.especialidad}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Ubicación</Form.Label>
          <Form.Control
            name="ubicacion"
            value={form.ubicacion}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Seguro médico</Form.Label>
          <Form.Control
            name="seguro_medico"
            value={form.seguro_medico}
            onChange={handleChange}
          />
        </Form.Group>
        <Button type="submit" disabled={saving}>
          {saving ? "Guardando..." : "Guardar Perfil"}
        </Button>
      </Form>
    </Container>
  );
}
