import React from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import heroImg from "../images/background1.jpg";
import "../styles/Home.css";

export default function Home() {
  return (
    <Container fluid className="p-0 m-0 min-vh-100 d-flex">
      <Row className="gx-0 flex-grow-1 w-100">
        {/* Imagen a la izquierda (oculta en mobile) */}
        <Col md={6} className="d-none d-md-block p-0">
          <img
            src={heroImg}
            alt="Ilustración gestión citas"
            className="hero-img"
          />
        </Col>

        {/* Bloque de bienvenida centrado */}
        <Col
          xs={12}
          md={6}
          className="d-flex align-items-center justify-content-center p-0"
          style={{
            minHeight: "100vh",
            background: "linear-gradient(120deg, #f8fafc 60%, #e7f1ff 100%)",
          }}
        >
          <Card
            className="border-0 shadow-lg welcome-card w-100"
            style={{ maxWidth: 420 }}
          >
            <Card.Body className="d-flex flex-column justify-content-center text-center">
              <Card.Title as="h1" className="mb-3 welcome-title">
                Bienvenido a QuickCita
              </Card.Title>
              <Card.Text className="mb-4 welcome-text">
                Tu sistema moderno para agendar, gestionar y optimizar tus citas
                médicas.
              </Card.Text>
              <div className="d-flex justify-content-center gap-2">
                <Button
                  as={Link}
                  to="/login"
                  size="lg"
                  variant="primary"
                  className="px-4"
                >
                  Iniciar Sesión
                </Button>
                <Button
                  as={Link}
                  to="/register"
                  size="lg"
                  variant="outline-primary"
                  className="px-4"
                >
                  Registro
                </Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
